import React from "react";
import ProductDetails from "./ProductDetails";
 import {useParams} from "react-router-dom"

function Product() {
  console.log(ProductDetails[0].image)
   const {category} = useParams()
  console.log(category)
   const categor = ProductDetails.filter(product => product.category === category)
   console.log(category)
  const productElements = categor.map((product) => (
    <div className="div-component" key={product.id}>
      <img src={product.image} />
      <h4>Brand : Levi's</h4>
      <h4>Size : M</h4>
      <h4>price : {product.price}</h4>
    </div>
  ));

  return (
    <div className="div-shirt">
     {productElements}
    </div>
  );
}

export default Product;
