import React from "react";
import ProductDetails from "./ProductDetails";
import { useParams } from "react-router-dom";

function Product() {
  //  console.log(ProductDetails)
  const { category, type } = useParams();
  console.log(category, type);
  const categor = ProductDetails.filter(
    (product) => product.category === category
  );
  console.log(categor);
  const typ = ProductDetails.filter( product => product.type === type)
  console.log(typ)
  let condition = categor.length > 0 ? categor : ProductDetails;
  const productElements = condition.map((product) => (
    <div className="div-component" key={product.id}>
      <img src={product.image} alt="" />
      <h4>Brand : Levi's</h4>
      <h4>Size : M</h4>
      <h4>price : {product.price}</h4>
    </div>
  ));

  return <div className="div-shirt">{productElements}</div>;
}

export default Product;
