import React from "react";
import ProductDetails from "../data/ProductData";
import { useParams } from "react-router-dom";
import ProductComponent from "../component/ProductComponent";
import { Switch, Route } from "react-router-dom";
import ProductDetail from "./ProductDetail";

function Product() {
  const { category, type } = useParams();
  // console.log(category, type);

  const categor = ProductDetails.filter(
    (product) => product.category === category
  );
  // console.log(categor);

  const typ = ProductDetails.filter((product) => product.type === type);
  // console.log(typ);
  let condition =
    typ.length > 0 ? typ : categor.length > 0 ? categor : ProductDetails;
  // console.log(condition);

  const productElements = condition.map((product) => (
    <ProductComponent key={product.id} {...product} />
  ));

  return (
    <>
      <div className="div-shirt">{productElements}</div>
      <Switch>
        <Route>
          <ProductDetail />
        </Route>
      </Switch>
    </>
  );
}

export default Product;
