import React, { useContext } from "react";
import {
  MdOutlineLocalMall,
  MdOutlineFavoriteBorder,
  MdFavoriteBorder,
  MdPermIdentity,
} from "react-icons/md";

import { Link } from "react-router-dom";
import { ProductContext } from "../Context";
import WishlistPage from "../pages/WishlistPage";

function ProductComponent({ id, image, price, category, type }) {
  //   console.log(props);
 const addWish = useContext(ProductContext)


  

  return (
    <div className="div-productcomponent">
      <Link to={`/${category}/${type}/${id}-detailpage`}>
        <div>
          <img src={image} alt="" />
          <h4>Brand : Levi's</h4>
          <h4>Size : M</h4>
          <h4>price : {price}</h4>
        </div>
      </Link>
      <button onClick={addWish} >
        <span>
          <MdOutlineFavoriteBorder />
        </span>
        wishlist
      </button>
    </div>
  );
}

export default ProductComponent;
