import React, { useContext } from "react";
import {
  MdOutlineLocalMall,
  MdOutlineFavoriteBorder,
  MdOutlineFavorite,
  MdPermIdentity,
} from "react-icons/md";

import { Link } from "react-router-dom";
import { ProductContext } from "../Context";
import WishlistPage from "../pages/WishlistPage";

function ProductComponent({ id, image, price, category, type, isFavorite }) {
  //   console.log(props);
  const { addWish, wishlistButton } = useContext(ProductContext);
  console.log(wishlistButton);
  
  // const toggle = wishlistButton.find(product => product.id == id)
  // console.log(toggle)


  return (
    <div className="div-productcomponent">
      <Link to={`/${category}/${type}/${id}-detailpage`}>
        <div>
          <img src={image} alt="" />
          <h4>Brand : Levi's</h4>
          <h4>Size : M</h4>
          <h4>price : {price}</h4>
        </div>
      </Link>
      {isFavorite ? (
        <button className="wishbutton-clicked">
          <span>
            <MdOutlineFavorite />
          </span>
          wishlist
        </button>
      ) : (
        <button onClick={() => addWish(id)}>
          <span>
            <MdOutlineFavoriteBorder />
          </span>
          wishlist
        </button>
      )}
    </div>
  );
}

export default ProductComponent;
