import { set } from "lodash";
import React, { useState } from "react";
import ProductData from "./data/ProductData";
import Product from "./pages/product";

const ProductContext = React.createContext();

function ProductContextProvider(props) {
  const [Wishlist, setWishlist] = useState([]);
  // const [wishlistButton, setWishlistButton] = useState(false);
  // console.log(wishlistButton);
  console.log(Wishlist);

  function addWish(id) {
    setWishlist((prevalue) => [
      ...prevalue,
      ProductData.find((Product) => Product.id == id),
    ]);
    
  }
  function removeWish(id) {
    console.log(id);
    setWishlist((prevalue) => prevalue.filter((Product) => Product.id !== id));
  }

  return (
    <ProductContext.Provider
      value={{ addWish, Wishlist, removeWish }}
    >
      {props.children}
    </ProductContext.Provider>
  );
}

export { ProductContext, ProductContextProvider };
