import React from "react";
import ProductDetails from "../data/ProductDetails";
import { useParams } from "react-router-dom";

function Product() {
  const { category, type } = useParams();
  console.log(category, type);

  const categor = ProductDetails.filter(
    (product) => product.category === category
  );
  console.log(categor);

  const typ = ProductDetails.filter((product) => product.type === type);
  console.log(typ);
  let condition =
    typ.length > 0 ? typ : categor.length > 0 ? categor : ProductDetails;
  console.log(condition);

  const productElements = condition.map((product) => (
    <div className="div-component" key={product.id}>
      <img src={product.image} alt="" />
      <h4>Brand : Levi's</h4>
      <h4>Size : M</h4>
      <h4>price : {product.price}</h4>
    </div>
  ));

  function handleDetail() {
    console.log("Detail Component")
    
  }

  return <div onClick={handleDetail} className="div-shirt">{productElements}</div>;
}

export default Product;
