import React from "react";
import { useParams } from "react-router-dom";
import ProductData from "../data/ProductData";

import {
  MdGrade,
  MdOutlineLocalMall,
  MdOutlineFavoriteBorder,
  MdFavoriteBorder,
  MdPermIdentity,
} from "react-icons/md";
function ProductDetail() {
  const { category, type, id } = useParams();
  // console.log(id);

  const detail = ProductData.filter((product) => product.id == id);
    const [image] = detail
  console.log(image);

  return (
    <div className="div-detailpage">
      <img src={detail[0].image} alt="photo" />
      <div>
        <h2>Title: {detail[0].title}</h2>
        <h3>Category: {detail[0].category}</h3>
        <h3>Type: {detail[0].type}</h3>
        <h4>Description: {detail[0].description}</h4>
        <h4>
          <span>
            rating: {detail[0].rating.rate}
            <MdGrade />
          </span>
          <span>count: {detail[0].rating.count}</span>
        </h4>
        <span>
          <button>
            <span>
              <MdOutlineLocalMall />
            </span>
            add to bag
          </button>
          <button>
            <span>
              <MdOutlineFavoriteBorder />
            </span>
            wishlist
          </button>
        </span>
      </div>
    </div>
  );
}

export default ProductDetail;
