import React from "react";
import { useParams } from "react-router-dom";
import ProductData from "../data/ProductData";

function ProductDetail() {
  const { category, type, id } = useParams();
  console.log(id);

  const detail = ProductData.filter((product) => product.id == id);

  console.log(detail.title);

  return (
    <>
      <img src={detail.image} alt="photo" />
      <div className="div-detailpage">
        <h1>Title :{detail.title}</h1>
        <h2>Category : {detail.category}</h2>
        <h3>Type : {detail.type}</h3>
        <h4>Description : {detail.description}</h4>
        {/* <h4>
          <span>rating:{detail.rating}</span>
          <span>count:{detail.rating}</span>
        </h4> */}
      </div>
    </>
  );
}

export default ProductDetail;
